mapper.select:
select username from step.user
where team_id = #{teamId}
and role_id = 1

select username from step.user
where team_id = #{teamId}
  and role_id = 2

mapper.update:
update step.user
set team_id = #{teamId}
where user_id = #{userId}

update step.user
set team_id = null
where user_id = #{userId}

mapper.select:
select count(*)
from team
where admin_id = #{adminId}
